LED Blink.code:
#define LED 13

void setup(){
  pinMode(LED, OUTPUT);
}

void loop(){
  digitalWrite(LED, HIGH);
  delay(1000);
  digitalWrite(LED, LOW);
  delay(1000);
}

Two LEDs Alternately Blink.code:
#define LED1 12
#define LED2 13

void setup(){
  pinMode(LED1, OUTPUT);
  pinMode(LED2, OUTPUT);
}

void loop(){
  digitalWrite(LED1, HIGH);
  digitalWrite(LED2, LOW);
  delay(1000);
  digitalWrite(LED1, LOW);
  digitalWrite(LED2, HIGH);
  delay(1000);
}

Button Control LED.code:
#define LED 13
#define BTN 2

void setup(){
  pinMode(LED, OUTPUT);
  pinMode(BTN, INPUT);
}

void loop(){
  int val = digitalRead(BTN);
  if(val == HIGH){
    digitalWrite(LED, HIGH);
  } else {
    digitalWrite(LED, LOW);
  }
}

Traffic Light.code:
#define R 8
#define Y 9
#define G 10

void setup(){
  pinMode(R,OUTPUT);
  pinMode(Y,OUTPUT);
  pinMode(G,OUTPUT);
}

void loop(){
  digitalWrite(R,HIGH);
  delay(3000);
  digitalWrite(R,LOW);

  digitalWrite(Y,HIGH);
  delay(1000);
  digitalWrite(Y,LOW);

  digitalWrite(G,HIGH);
  delay(3000);
  digitalWrite(G,LOW);
}

LED Fade using PWM.code:
#define LED 9

void setup(){
  pinMode(LED, OUTPUT);
}

void loop(){
  for(int i=0;i<=255;i++){
    analogWrite(LED, i);
    delay(10);
  }
  for(int i=255;i>=0;i--){
    analogWrite(LED, i);
    delay(10);
  }
}

